// index.js
const countDownDateAndTime = (conduct_date) => {
    const now = new Date();
    const targetDate = new Date(conduct_date); // Set your target date and time here
    const timeDifference = targetDate.getTime() - now.getTime();

    if (timeDifference > 0) {
        const days = Math.floor(timeDifference / (1000 * 60 * 60 * 24));
        const hours = Math.floor(
            (timeDifference % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)
        );
        const minutes = Math.floor(
            (timeDifference % (1000 * 60 * 60)) / (1000 * 60)
        );
        const seconds = Math.floor((timeDifference % (1000 * 60)) / 1000);

        return { days, hours, minutes, seconds };
    } else {
        return { days: 0, hours: 0, minutes: 0, seconds: 0 }; // Return default values if the difference is negative
    }
};

module.exports = { countDownDateAndTime };
